		TransferService transferService = (TransferService) appContext
				.getBean("TransferService");
		String transId = getParameter("transId");
		String frequenceType = Utils.null2EmptyWithTrim(this
				.getParameter("frequenceType"));
		String frequenceDays = Utils.null2EmptyWithTrim(this
				.getParameter("frequenceDays"));
		String scheduleName = Utils.null2EmptyWithTrim(this
				.getParameter("scheduleName"));
		String status = Utils.null2EmptyWithTrim(this.getParameter("status"));

